function getRandomItem(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

// Filter items based on unique keys from the merged inputs
const videoItems = items.filter(item => item.json.BackgroundURL !== undefined);
const musicItems = items.filter(item => item.json.MusicURL !== undefined);
const quoteItems = items.filter(item => item.json.Qoute !== undefined);

// Debug logs to check counts in the execution log
console.log("Video Items count: " + videoItems.length);
console.log("Music Items count: " + musicItems.length);
console.log("Quote Items count: " + quoteItems.length);

if (videoItems.length === 0 || musicItems.length === 0 || quoteItems.length === 0) {
  throw new Error("One or more input arrays are empty. Check your previous nodes.");
}

const selectedVideo = getRandomItem(videoItems);
const selectedMusic = getRandomItem(musicItems);
const selectedQuote = getRandomItem(quoteItems);

// Return the combined selected items
return [{
  video: selectedVideo.json,
  music: selectedMusic.json,
  quote: selectedQuote.json
}];

// Define separate configuration for the quote and the author
const quoteFont = "Kanit-Italic.ttf";      // Font for the quote
const quoteFontSize = 70;
const authorFont = "Kanit-Italic.ttf";         // Font for the author (ensure this supports Thai)
const authorFontSize = 50;
const fontColor = "white";
const lineHeightMultiplier = 1.1;
const videoWidth = 1080;
const margin = 40;  // Gap from left and right edges

// Effective width for the quote text (accounting for left/right margins)
const effectiveVideoWidth = videoWidth - 2 * margin;

// Estimate average character width based on quoteFontSize (this is a rough estimate)
const avgCharWidth = quoteFontSize * 0.6;
const maxCharsPerLine = Math.floor(effectiveVideoWidth / avgCharWidth);

// Retrieve the quote transcript and author from the "Merge" node
const transcript = $node["Merge File Selection Data"].json["Qoute"];
if (!transcript) {
  throw new Error("Quote not found");
}
const author = $node["Merge File Selection Data"].json["Author"];
if (!author) {
  throw new Error("Author not found");
}

// Split the transcript into words and group them into lines based on maxCharsPerLine
const words = transcript.split(' ');
const lines = [];
let currentLine = "";
let currentCharCount = 0;

words.forEach(word => {
  const wordLength = word.length;
  const additionalSpace = currentLine ? 1 : 0;
  const potentialLength = currentCharCount + additionalSpace + wordLength;
  if (potentialLength <= maxCharsPerLine) {
    currentLine += (currentLine ? " " : "") + word;
    currentCharCount = potentialLength;
  } else {
    lines.push(currentLine);
    currentLine = word;
    currentCharCount = wordLength;
  }
});
if (currentLine) {
  lines.push(currentLine);
}

// Calculate layout for the quote block
const lineHeight = quoteFontSize * lineHeightMultiplier;
const totalHeight = lines.length * lineHeight;

// Build drawtext commands for each quote line (centered horizontally)
// Each line is positioned so that the entire quote block is vertically centered.
const quoteCommands = lines.map((line, index) => {
  // Escape any single quotes in the line
  const escapedLine = line.replace(/'/g, "\\'");
  return `drawtext=fontfile=${quoteFont}:text='${escapedLine}':fontsize=${quoteFontSize}:fontcolor=${fontColor}:x=(w-text_w)/2:y=((h-${totalHeight})/2)+(${index}*${lineHeight})`;
});

// Build the drawtext command for the author
// Place the author text below the quote block with a small gap (e.g. 20 pixels)
// Align it to the right by setting x = w - text_w - margin.
const authorY = `((h-${totalHeight})/2)+(${lines.length}*${lineHeight})+20`;
const escapedAuthor = author.replace(/'/g, "\\'");
const authorCommand = `drawtext=fontfile=${authorFont}:text='${escapedAuthor}':fontsize=${authorFontSize}:fontcolor=${fontColor}:x=w-text_w-${margin}:y=${authorY}`;

// Combine all commands (separated by commas) into one drawtext filter string.
const fullDrawTextFilter = quoteCommands.concat(authorCommand).join(", ");

// Return the prepared filter string for insertion into your FFmpeg command.
return {
  json: {
    drawText: fullDrawTextFilter
  }
};

